import React, { useState, useEffect } from "react";
import {
  FaHotel,
  FaUtensils,
  FaGasPump,
  FaShoppingBag,
  FaStore,
  FaInfoCircle,
} from "react-icons/fa";
import { recommendationsService } from "../services/api";

const Recommendations = ({ userLocation, route }) => {
  const [recommendations, setRecommendations] = useState({
    hotels: [],
    restaurants: [],
    gasStations: [],
    shops: [],
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState("hotels");
  const [searchRadius, setSearchRadius] = useState(5); // km

  useEffect(() => {
    if (userLocation) {
      fetchRecommendations();
    }
  }, [userLocation, searchRadius, selectedCategory]);

  const fetchRecommendations = async () => {
    if (!userLocation) return;

    setIsLoading(true);
    setError(null);

    try {
      const response = await recommendationsService.getRecommendations(
        userLocation,
        selectedCategory,
        searchRadius,
        route
      );

      setRecommendations((prev) => ({
        ...prev,
        [selectedCategory]: response.data.places,
      }));
    } catch (err) {
      console.error("Error fetching recommendations:", err);
      setError("Failed to fetch recommendations. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  // Get icon based on category
  const getCategoryIcon = (category) => {
    switch (category) {
      case "hotels":
        return <FaHotel className="w-5 h-5" />;
      case "restaurants":
        return <FaUtensils className="w-5 h-5" />;
      case "gasStations":
        return <FaGasPump className="w-5 h-5" />;
      case "shops":
        return <FaShoppingBag className="w-5 h-5" />;
      default:
        return <FaStore className="w-5 h-5" />;
    }
  };

  // Format rating stars
  const renderRatingStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;

    for (let i = 0; i < fullStars; i++) {
      stars.push(
        <span key={`star-${i}`} className="text-yellow-500">
          ★
        </span>
      );
    }

    if (hasHalfStar) {
      stars.push(
        <span key="half-star" className="text-yellow-500">
          ✮
        </span>
      );
    }

    const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);
    for (let i = 0; i < emptyStars; i++) {
      stars.push(
        <span key={`empty-star-${i}`} className="text-gray-300">
          ☆
        </span>
      );
    }

    return stars;
  };

  // Calculate distance in km
  const calculateDistance = (place) => {
    if (!place.geometry || !place.geometry.location) return "N/A";

    const lat1 = userLocation.lat;
    const lon1 = userLocation.lng;
    const lat2 = place.geometry.location.lat;
    const lon2 = place.geometry.location.lng;

    const R = 6371; // Radius of the earth in km
    const dLat = deg2rad(lat2 - lat1);
    const dLon = deg2rad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(deg2rad(lat1)) *
        Math.cos(deg2rad(lat2)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distance = R * c;

    return distance.toFixed(1);
  };

  const deg2rad = (deg) => {
    return deg * (Math.PI / 180);
  };

  return (
    <div className="py-6">
      {!userLocation ? (
        <div className="flex flex-col items-center justify-center p-8 bg-yellow-50 rounded-lg border border-yellow-200">
          <FaInfoCircle className="text-yellow-500 text-3xl mb-4" />
          <h3 className="text-lg font-medium text-yellow-800">
            No Location Selected
          </h3>
          <p className="text-yellow-700 mt-2 text-center">
            Please use the Map tab to select a starting location first.
          </p>
        </div>
      ) : (
        <div>
          <div className="mb-6">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">
              Recommendations near {userLocation.name || "your location"}
            </h2>

            <div className="flex flex-wrap gap-3 mb-4">
              <button
                className={`px-4 py-2 rounded-full flex items-center ${
                  selectedCategory === "hotels"
                    ? "bg-teal-500 text-white"
                    : "bg-gray-200 hover:bg-gray-300 text-gray-700"
                }`}
                onClick={() => setSelectedCategory("hotels")}
              >
                <FaHotel className="mr-2" /> Hotels
              </button>
              <button
                className={`px-4 py-2 rounded-full flex items-center ${
                  selectedCategory === "restaurants"
                    ? "bg-teal-500 text-white"
                    : "bg-gray-200 hover:bg-gray-300 text-gray-700"
                }`}
                onClick={() => setSelectedCategory("restaurants")}
              >
                <FaUtensils className="mr-2" /> Restaurants
              </button>
              <button
                className={`px-4 py-2 rounded-full flex items-center ${
                  selectedCategory === "gasStations"
                    ? "bg-teal-500 text-white"
                    : "bg-gray-200 hover:bg-gray-300 text-gray-700"
                }`}
                onClick={() => setSelectedCategory("gasStations")}
              >
                <FaGasPump className="mr-2" /> Gas Stations
              </button>
              <button
                className={`px-4 py-2 rounded-full flex items-center ${
                  selectedCategory === "shops"
                    ? "bg-teal-500 text-white"
                    : "bg-gray-200 hover:bg-gray-300 text-gray-700"
                }`}
                onClick={() => setSelectedCategory("shops")}
              >
                <FaShoppingBag className="mr-2" /> Shops
              </button>
            </div>

            <div className="flex items-center mb-5">
              <label htmlFor="radius" className="mr-3 text-gray-700">
                Search radius:
              </label>
              <select
                id="radius"
                value={searchRadius}
                onChange={(e) => setSearchRadius(Number(e.target.value))}
                className="px-2 py-1 border border-gray-300 rounded focus:outline-none focus:border-teal-500"
              >
                <option value={1}>1 km</option>
                <option value={3}>3 km</option>
                <option value={5}>5 km</option>
                <option value={10}>10 km</option>
                <option value={20}>20 km</option>
              </select>
            </div>
          </div>

          {isLoading ? (
            <div className="flex justify-center items-center py-10">
              <svg
                className="animate-spin h-8 w-8 text-teal-500"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
              >
                <circle
                  className="opacity-25"
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="currentColor"
                  strokeWidth="4"
                ></circle>
                <path
                  className="opacity-75"
                  fill="currentColor"
                  d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                ></path>
              </svg>
            </div>
          ) : error ? (
            <div className="bg-red-50 p-4 rounded-md border border-red-200 text-red-700">
              {error}
            </div>
          ) : recommendations[selectedCategory].length === 0 ? (
            <div className="bg-gray-50 p-4 rounded-md border border-gray-200 text-gray-700">
              No {selectedCategory} found within {searchRadius} km. Try
              increasing the search radius.
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {recommendations[selectedCategory].map((place) => (
                <div
                  key={place.id}
                  className="border border-gray-200 rounded-lg overflow-hidden shadow-sm hover:shadow-md transition-shadow duration-200"
                >
                  {place.photos && place.photos.length > 0 ? (
                    <img
                      src={
                        place.photos[0].url ||
                        `https://via.placeholder.com/300x200?text=${encodeURIComponent(
                          place.name
                        )}`
                      }
                      alt={place.name}
                      className="w-full h-48 object-cover"
                    />
                  ) : (
                    <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                      {getCategoryIcon(selectedCategory)}
                    </div>
                  )}
                  <div className="p-4">
                    <div className="flex justify-between items-start">
                      <h3 className="font-bold text-lg mb-1">{place.name}</h3>
                      <span className="text-sm bg-teal-100 text-teal-800 px-2 py-1 rounded">
                        {calculateDistance(place)} km
                      </span>
                    </div>
                    <div className="flex items-center mb-2">
                      <div>{renderRatingStars(place.rating || 0)}</div>
                      <span className="ml-1 text-sm text-gray-600">
                        ({place.user_ratings_total || 0})
                      </span>
                    </div>
                    <p className="text-gray-600 text-sm mb-3 line-clamp-2">
                      {place.vicinity || place.formatted_address}
                    </p>

                    {place.opening_hours && (
                      <div className="text-sm text-gray-700 mb-3">
                        {place.opening_hours.open_now ? (
                          <span className="text-green-600 font-medium">
                            Open Now
                          </span>
                        ) : (
                          <span className="text-red-600 font-medium">
                            Closed
                          </span>
                        )}
                      </div>
                    )}

                    {place.price_level && (
                      <div className="mb-3">
                        <span className="text-yellow-600 font-medium">
                          {"$".repeat(place.price_level)}
                          <span className="text-gray-300">
                            {"$".repeat(4 - place.price_level)}
                          </span>
                        </span>
                      </div>
                    )}

                    <button
                      onClick={() =>
                        window.open(
                          `https://www.google.com/maps/place/?q=place_id:${place.place_id}`,
                          "_blank"
                        )
                      }
                      className="w-full mt-2 bg-teal-500 hover:bg-teal-600 text-white py-2 px-4 rounded transition-colors duration-200"
                    >
                      View on Google Maps
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Recommendations;
