import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

// Create axios instance with base URL
const apiClient = axios.create({
  baseURL: API_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

// Routes API service
export const routesService = {
  // Calculate optimal route
  calculateRoute: (source, destination, algorithm = "a-star") => {
    return apiClient.post("/routes/calculate", {
      source,
      destination,
      algorithm,
    });
  },

  // Calculate route with preferences
  calculateRouteWithPreferences: (source, destination, preferences) => {
    return apiClient.post("/routes/preferences", {
      source,
      destination,
      preferences,
    });
  },

  // Get offline route data
  getOfflineRoute: (routeId) => {
    return apiClient.get(`/routes/offline/${routeId}`);
  },

  // Save route for offline access
  saveRouteForOffline: (routeData) => {
    return apiClient.post("/routes/save", routeData);
  },
};

// Recommendations API service
export const recommendationsService = {
  // Get place recommendations
  getPlaceRecommendations: (lat, lng, radius, type) => {
    return apiClient.get("/recommendations/places", {
      params: { lat, lng, radius, type },
    });
  },

  // Get hotel recommendations
  getHotelRecommendations: (lat, lng, radius, priceLevel) => {
    return apiClient.get("/recommendations/hotels", {
      params: { lat, lng, radius, priceLevel },
    });
  },

  // Get restaurant recommendations
  getRestaurantRecommendations: (lat, lng, radius, cuisine) => {
    return apiClient.get("/recommendations/restaurants", {
      params: { lat, lng, radius, cuisine },
    });
  },

  // Get gas station recommendations
  getGasStationRecommendations: (route, maxDetour) => {
    return apiClient.get("/recommendations/gas-stations", {
      params: { route: JSON.stringify(route), maxDetour },
    });
  },
};

// Predictions API service
export const predictionsService = {
  // Predict travel time
  predictTravelTime: (source, destination, departureTime, traffic) => {
    return apiClient.post("/predictions/travel-time", {
      source,
      destination,
      departureTime,
      traffic,
    });
  },

  // Predict traffic conditions
  predictTraffic: (route, time) => {
    return apiClient.post("/predictions/traffic", { route, time });
  },

  // Get historical travel data
  getHistoricalData: (source, destination, limit) => {
    return apiClient.get("/predictions/historical-data", {
      params: { source, destination, limit },
    });
  },
};

export default { routesService, recommendationsService, predictionsService };
